mapper.select:
select * from ( SELECT b.frozen_day as days, tt3.`name`, b.area_id, b.concentrator_id, b.pn, ROUND( c.rateseq1 * tt3.pt * tt3.ct, 0 ) rateseq1, ROUND( c.rateseq2 * tt3.pt * tt3.ct, 0 ) rateseq2, ROUND( c.rateseq3 * tt3.pt * tt3.ct, 0 ) rateseq3, ROUND( c.rateseq4 * tt3.pt * tt3.ct, 0 ) rateseq4, round( ( b.totalPositiveActivePower ) * tt3.pt * tt3.ct, 0 ) totalPositiveActivePower, round( ( d .totalPositiveReactivePower ) * tt3.pt * tt3.ct, 0 ) totalPositiveReactivePower FROM t_030_type_two_data_fn5 b, ( SELECT id, max( CASE rate_seq WHEN '1' THEN a.positiveActivePower ELSE '' END ) AS rateseq1, max( CASE rate_seq WHEN '2' THEN a.positiveActivePower ELSE '' END ) AS rateseq2, max( CASE rate_seq WHEN '3' THEN a.positiveActivePower ELSE '' END ) AS rateseq3, max( CASE rate_seq WHEN '4' THEN a.positiveActivePower ELSE '' END ) AS rateseq4 FROM t_030_type_two_data_fn5_rate a GROUP BY id ) c, t_032_type_two_data_fn6 d, t_202_pn_info tt3 WHERE b.id = c.id AND b.frozen_day IS NOT NULL AND b.frozen_day = d.frozen_day AND b.area_id = d.area_id AND b.concentrator_id = d.concentrator_id AND b.pn = d.pn and b.area_id = tt3.area_id and b.concentrator_id = tt3.concentrator_id and b.pn = tt3.pn ) ct where 1=1
and ct.area_id = #{areaId ,jdbcType=VARCHAR}
and ct.days = #{date ,jdbcType=VARCHAR}
and (
( ct.concentrator_id = #{item.concentratorId,jdbcType=VARCHAR} and ct.pn in
#{pn,jdbcType=VARCHAR}
)
)
order by ct.days desc
limit ${limitStart} , ${limitEnd}

select count(1) from ( SELECT b.frozen_day as days, tt3.`name`, b.area_id, b.concentrator_id, b.pn, ROUND( c.rateseq1 * tt3.pt * tt3.ct, 0 ) rateseq1, ROUND( c.rateseq2 * tt3.pt * tt3.ct, 0 ) rateseq2, ROUND( c.rateseq3 * tt3.pt * tt3.ct, 0 ) rateseq3, ROUND( c.rateseq4 * tt3.pt * tt3.ct, 0 ) rateseq4, round( ( b.totalPositiveActivePower ) * tt3.pt * tt3.ct, 0 ) totalPositiveActivePower, round( ( d .totalPositiveReactivePower ) * tt3.pt * tt3.ct, 0 ) totalPositiveReactivePower FROM t_030_type_two_data_fn5 b, ( SELECT id, max( CASE rate_seq WHEN '1' THEN a.positiveActivePower ELSE '' END ) AS rateseq1, max( CASE rate_seq WHEN '2' THEN a.positiveActivePower ELSE '' END ) AS rateseq2, max( CASE rate_seq WHEN '3' THEN a.positiveActivePower ELSE '' END ) AS rateseq3, max( CASE rate_seq WHEN '4' THEN a.positiveActivePower ELSE '' END ) AS rateseq4 FROM t_030_type_two_data_fn5_rate a GROUP BY id ) c, t_032_type_two_data_fn6 d, t_202_pn_info tt3 WHERE b.id = c.id AND b.frozen_day IS NOT NULL AND b.frozen_day = d.frozen_day AND b.area_id = d.area_id AND b.concentrator_id = d.concentrator_id AND b.pn = d.pn and b.area_id = tt3.area_id and b.concentrator_id = tt3.concentrator_id and b.pn = tt3.pn ) ct where 1=1
and ct.area_id = #{areaId ,jdbcType=VARCHAR}
and ct.days = #{date ,jdbcType=VARCHAR}
and (
( ct.concentrator_id = #{item.concentratorId,jdbcType=VARCHAR} and ct.pn in
#{pn,jdbcType=VARCHAR}
