select * from ( select DATE_FORMAT(t1.sendTime,'%Y%m%d') days,t1.area_id,t1.concentrator_id,t1.pn,t1.harmonic_seq,t3.`name`,max(t1.A_harmonicVoltage_rate+0) as AharmonicVoltagerate,max(t1.B_harmonicVoltage_rate+0) as BharmonicVoltagerate,max(t1.C_harmonicVoltage_rate+0) as CharmonicVoltagerate,ROUND(max(t2.A_harmonicCurrent+0)*t3.ct,1) as AharmonicCurrent,ROUND(max(t2.B_harmonicCurrent+0)*t3.ct,1) as BharmonicCurrent,ROUND(max(t2.C_harmonicCurrent+0)*t3.ct,1) as CharmonicCurrent from (select a.area_id,a.concentrator_id,a.pn,a.sendTime,b.A_harmonicVoltage_rate,b.B_harmonicVoltage_rate,b.C_harmonicVoltage_rate,b.harmonic_seq from t_014_type_one_data_fn58 a,t_015_type_one_data_fn58_harmonic b where a.id = b.id) t1,(select a.area_id,a.concentrator_id,a.pn,a.sendTime,b.A_harmonicCurrent,b.B_harmonicCurrent,b.C_harmonicCurrent,b.harmonic_seq from t_012_type_one_data_fn57 a,t_013_type_one_data_fn57_harmonic b where a.id = b.id) t2,t_202_pn_info t3 where t1.sendTime = t2.sendTime and t1.harmonic_seq = t2.harmonic_seq and t1.area_id = t2.area_id and t1.concentrator_id = t2.concentrator_id and t1.pn = t2.pn and t1.sendTime is not null and t1.area_id = t3.area_id and t1.concentrator_id = t3.concentrator_id and t1.pn = t3.pn GROUP BY days,t1.area_id,t1.concentrator_id,t1.pn,t1.harmonic_seq ) ct where 1=1
and ct.area_id = #{areaId ,jdbcType=VARCHAR}
and ct.concentrator_id in
#{item}
and ct.days = #{date ,jdbcType=VARCHAR}
and ct.pn = #{pn ,jdbcType=VARCHAR}
and ct.harmonic_seq = #{harmonicseq ,jdbcType=VARCHAR}
order by (ct.harmonic_seq+0)
limit ${limitStart} , ${limitEnd}

