	select DISTINCT days,a.area_id,a.concentrator_id,a.pn,a.totalPositiveMaxActivePower,a.totalPositiveMaxActivePowerTime,c.`name` from t_016_type_one_data_fn145 a ,(select date_format(clientOperationTime,'%Y%m') AS days, area_id,concentrator_id,pn,max(totalPositiveMaxActivePowerTime + 0) as totalPositiveMaxActivePowerTime from t_016_type_one_data_fn145 where totalPositiveMaxActivePowerTime is not null group by  days,area_id ,concentrator_id,pn) b , t_202_pn_info c  where a.area_id = b.area_id and a.concentrator_id = b.concentrator_id and a.pn = b.pn and (a.totalPositiveMaxActivePowerTime+0) = b.totalPositiveMaxActivePowerTime and date_format(a.clientOperationTime,'%Y%m') = days and b.area_id = c.area_id and b.concentrator_id = c.concentrator_id and b.pn = c.pn    
    
		  and a.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
		  and a.concentrator_id in
		    
	      		#{item}  
	      
	
	
		  and days = #{date ,jdbcType=VARCHAR} 
	
	order by days desc
	
      limit ${limitStart} , ${limitEnd}

    select count(1) from (
        select DISTINCT days,a.area_id,a.concentrator_id,a.pn,a.totalPositiveMaxActivePower,a.totalPositiveMaxActivePowerTime,c.`name` from t_016_type_one_data_fn145 a ,(select date_format(clientOperationTime,'%Y%m') AS days, area_id,concentrator_id,pn,max(totalPositiveMaxActivePowerTime + 0) as totalPositiveMaxActivePowerTime from t_016_type_one_data_fn145 where totalPositiveMaxActivePowerTime is not null group by  days,area_id ,concentrator_id,pn) b , t_202_pn_info c  where a.area_id = b.area_id and a.concentrator_id = b.concentrator_id and a.pn = b.pn and (a.totalPositiveMaxActivePowerTime+0) = b.totalPositiveMaxActivePowerTime and date_format(a.clientOperationTime,'%Y%m') = days and b.area_id = c.area_id and b.concentrator_id = c.concentrator_id and b.pn = c.pn
    ) ct where 1= 1
    
		  and ct.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
