mapper.select:
    select * from(
		SELECT date_format(a.clientOperationTime,'%Y%m') AS days, a.area_id, a.concentrator_id, a.pn, a.totalPositiveMaxActivePower, a.totalPositiveMaxActivePowerTime, a.`name` FROM ( SELECT t3.clientOperationTime, t3.area_id, t3.concentrator_id, t3.pn, t3.totalPositiveMaxActivePower, t3.totalPositiveMaxActivePowerTime, t3.`name` FROM ( SELECT t1.`name`, t2.area_id, t2.concentrator_id, t2.pn, t2.totalPositiveMaxActivePower, t2.totalPositiveMaxActivePowerTime, t2.clientOperationTime FROM t_202_pn_info t1 INNER JOIN t_016_type_one_data_fn145 t2 ON t1.area_id = t2.area_id AND t1.concentrator_id = t2.concentrator_id AND t1.pn = t2.pn ORDER BY (t2.clientOperationTime + 0) DESC ) t3 GROUP BY t3.area_id, t3.concentrator_id, t3.pn ) a GROUP BY days, a.area_id, a.concentrator_id, a.pn   
    ) ct where 1=1
    
		  and ct.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
		  and ct.days = #{date ,jdbcType=VARCHAR} 
	
	
        and (
        
            (
             ct.concentrator_id = #{item.concentratorId,jdbcType=VARCHAR}
             and ct.pn in 
               
	      		#{pn,jdbcType=VARCHAR}  
	         
            )
        
        )
    
	order by ct.days desc
	
      limit ${limitStart} , ${limitEnd}

    select count(1) from (
        SELECT date_format(a.clientOperationTime,'%Y%m') AS days, a.area_id, a.concentrator_id, a.pn, a.totalPositiveMaxActivePower, a.totalPositiveMaxActivePowerTime, a.`name` FROM ( SELECT t3.clientOperationTime, t3.area_id, t3.concentrator_id, t3.pn, t3.totalPositiveMaxActivePower, t3.totalPositiveMaxActivePowerTime, t3.`name` FROM ( SELECT t1.`name`, t2.area_id, t2.concentrator_id, t2.pn, t2.totalPositiveMaxActivePower, t2.totalPositiveMaxActivePowerTime, t2.clientOperationTime FROM t_202_pn_info t1 INNER JOIN t_016_type_one_data_fn145 t2 ON t1.area_id = t2.area_id AND t1.concentrator_id = t2.concentrator_id AND t1.pn = t2.pn ORDER BY (t2.clientOperationTime + 0) DESC ) t3 GROUP BY t3.area_id, t3.concentrator_id, t3.pn ) a GROUP BY days, a.area_id, a.concentrator_id, a.pn 
    ) ct where 1= 1
    
		  and ct.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
		  and ct.days = #{date ,jdbcType=VARCHAR} 
	
	
        and (
        
            (
             ct.concentrator_id = #{item.concentratorId,jdbcType=VARCHAR}
             and ct.pn in 
               
	      		#{pn,jdbcType=VARCHAR}  
	         
            )
        
        )
