	select a.*,c.`name`,c.ct,c.pt from t_003_type_one_data_fn25 a,(select concentrator_id,pn,max(clientOperationTime + 0) clientOperationTime from t_003_type_one_data_fn25 group by concentrator_id,pn) b ,t_202_pn_info c where a.concentrator_id = b.concentrator_id and a.pn = b.pn and a.clientOperationTime = b.clientOperationTime and  b.concentrator_id = c.concentrator_id and b.pn = c.pn
	
		  and a.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
		  and a.concentrator_id in
		    
	      		#{item}  
	      
	
	order by a.concentrator_id
	
      limit ${limitStart} , ${limitEnd}

    select count(1) as num from t_003_type_one_data_fn25 a,(select concentrator_id,pn,max(clientOperationTime + 0) clientOperationTime from t_003_type_one_data_fn25 group by concentrator_id,pn) b ,t_202_pn_info c where a.concentrator_id = b.concentrator_id and a.pn = b.pn and a.clientOperationTime = b.clientOperationTime and  b.concentrator_id = c.concentrator_id and b.pn = c.pn
	
		  and a.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
		  and a.concentrator_id in
		    
	      		#{item}

    select max(a.A_Voltage + 0)*b.pt as maxAVoltage,max(a.B_Voltage + 0)*b.pt as maxBVoltage,max(a.C_Voltage + 0)*b.pt as maxCVoltage,min(a.A_Voltage + 0)*b.pt as minAVoltage,min(a.B_Voltage + 0)*b.pt as minBVoltage,min(a.C_Voltage + 0)*b.pt as minCVoltage ,ROUND(max(a.A_Voltage + 0),1) as orgMaxAVoltage,ROUND(max(a.B_Voltage + 0),1) as orgMaxBVoltage,ROUND(max(a.C_Voltage + 0),1) as orgMaxCVoltage,ROUND(min(a.A_Voltage + 0),1) as orgMinAVoltage,ROUND(min(a.B_Voltage + 0),1) as orgMinBVoltage,ROUND(min(a.C_Voltage + 0),1) as orgMinCVoltage from t_003_type_one_data_fn25 a,t_202_pn_info b where 1=1 and a.area_id = b.area_id and a.concentrator_id = b.concentrator_id and a.pn = b.pn
	
		  and a.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
		  and a.concentrator_id = #{concentratorId ,jdbcType=VARCHAR}
	
	
		  and a.pn = #{pn ,jdbcType=VARCHAR}
	
	
		  and a.clientOperationTime like CONCAT(#{date ,jdbcType=VARCHAR},'%')

select max(a.A_Current + 0)*b.ct as maxACurrent,max(a.B_Current + 0)*b.ct as maxBCurrent,max(a.C_Current + 0)*b.ct as maxCCurrent,min(a.A_Current + 0)*b.ct as minACurrent,min(a.B_Current + 0)*b.ct as minBCurrent,min(a.C_Current + 0)*b.ct as minCCurrent  ,ROUND(max(a.A_Current + 0),3) as orgMaxACurrent,ROUND(max(a.B_Current + 0),3) as orgMaxBCurrent,ROUND(max(a.C_Current + 0),3) as orgMaxCCurrent,ROUND(min(a.A_Current + 0),3) as orgMinACurrent,ROUND(min(a.B_Current + 0),3) as orgMinBCurrent,ROUND(min(a.C_Current + 0),3) as orgMinCCurrent from t_003_type_one_data_fn25 a ,t_202_pn_info b where 1=1 and a.area_id = b.area_id and a.concentrator_id = b.concentrator_id and a.pn = b.pn	

	  and a.area_id = #{areaId ,jdbcType=VARCHAR} 


	  and a.concentrator_id = #{concentratorId ,jdbcType=VARCHAR}


	  and a.pn = #{pn ,jdbcType=VARCHAR}


	  and a.clientOperationTime like CONCAT(#{date ,jdbcType=VARCHAR},'%')

select max(a.A_PowerFactor + 0) as maxAPowerFactor,max(a.B_PowerFactor + 0) as maxBPowerFactor,max(a.C_PowerFactor + 0) as maxCPowerFactor,max(a.totalPowerFactor + 0)  as maxTotalPowerFactor,min(a.A_PowerFactor + 0) as minAPowerFactor,min(a.B_PowerFactor + 0) as minBPowerFactor,min(a.C_PowerFactor + 0) as minCPowerFactor,min(a.totalPowerFactor + 0) as minTotalPowerFactor, ROUND(max(a.A_PowerFactor + 0),1) as orgMaxAPowerFactor,ROUND(max(a.B_PowerFactor + 0),1) as orgMaxBPowerFactor,ROUND(max(a.C_PowerFactor + 0),1) as orgMaxCPowerFactor,ROUND(max(a.totalPowerFactor + 0),1)  as orgMaxTotalPowerFactor,ROUND(min(a.A_PowerFactor + 0),1) as orgMinAPowerFactor,ROUND(min(a.B_PowerFactor + 0),1) as orgMinBPowerFactor,ROUND(min(a.C_PowerFactor + 0),1) as orgMinCPowerFactor,ROUND(min(a.totalPowerFactor + 0),1) as orgMinTotalPowerFactor from t_003_type_one_data_fn25 a where 1=1	

	  and a.area_id = #{areaId ,jdbcType=VARCHAR} 


	  and a.concentrator_id = #{concentratorId ,jdbcType=VARCHAR}


	  and a.pn = #{pn ,jdbcType=VARCHAR}


	  and a.clientOperationTime like CONCAT(#{date ,jdbcType=VARCHAR},'%')

select max((a.totalActivePower + 0)*b.pt*b.ct) as maxTotalActivePower, min((a.totalActivePower + 0)*b.pt*b.ct) as minTotalActivePower,ROUND(avg((a.totalActivePower + 0)*b.pt*b.ct),2) as avgTotalActivePower,ROUND(max(a.totalActivePower + 0),4) as orgMaxTotalActivePower, ROUND(min(a.totalActivePower + 0),4) as orgMinTotalActivePower from t_003_type_one_data_fn25 a ,t_202_pn_info b where 1=1 and a.area_id = b.area_id and a.concentrator_id = b.concentrator_id and a.pn = b.pn and a.totalActivePower is not NULL	

	  and a.area_id = #{areaId ,jdbcType=VARCHAR} 


	  and a.concentrator_id = #{concentratorId ,jdbcType=VARCHAR}


	  and a.pn = #{pn ,jdbcType=VARCHAR}


	  and a.clientOperationTime like CONCAT(#{date ,jdbcType=VARCHAR},'%')
