    select * from (
	    select dm.`name`,dp.clientOperationTime,dm.weekstart,dm.weekend,dp.area_id,dp.concentrator_id,dp.pn,dp.totalPositiveActivePower,dp.rateseq1,dp.rateseq2,dp.rateseq3,dp.rateseq4,dm.totalPositiveMaxActivePower,dm.totalPositiveMaxActivePowerTime from (
		select b.clientOperationTime,b.area_id,b.concentrator_id,b.pn,b.totalPositiveActivePower,c.rateseq1,c.rateseq2,c.rateseq3,c.rateseq4  from t_004_type_one_data_fn33 b ,
		(select id,
		max(case rate_seq when '1' then a.positiveActivePower else '' end) as rateseq1,
		max(case rate_seq when '2' then a.positiveActivePower else '' end) as rateseq2,
		max(case rate_seq when '3' then a.positiveActivePower else '' end) as rateseq3,
		max(case rate_seq when '4' then a.positiveActivePower else '' end) as rateseq4
		from t_005_type_one_data_fn33_rate a
		group by id) c where b.id = c.id
		) dp,
		(
			select   a.clientOperationTime,b.weekstart,b.weekend,a.area_id,a.concentrator_id,a.pn,a.totalPositiveMaxActivePower,a.totalPositiveMaxActivePowerTime,c.`name`   from t_016_type_one_data_fn145 a ,(select date_add('1900-01-01',interval floor(datediff(str_to_date(clientOperationTime, '%Y%m%d%H%i%s'),'1900-01-01')/7)*7 day) as weekstart,  
            date_add('1900-01-01',interval floor(datediff(str_to_date(clientOperationTime, '%Y%m%d%H%i%s'),'1900-01-01')/7)*7+6 day)as weekend, area_id,concentrator_id,pn,max(totalPositiveMaxActivePowerTime + 0) as totalPositiveMaxActivePowerTime from t_016_type_one_data_fn145 where totalPositiveMaxActivePowerTime is not null group by  floor(datediff(str_to_date(clientOperationTime, '%Y%m%d%H%i%s'),'1900-01-01')/7),area_id ,concentrator_id,pn) b , t_202_pn_info c  where a.area_id = b.area_id and a.concentrator_id = b.concentrator_id and a.pn = b.pn and (a.totalPositiveMaxActivePowerTime+0) = b.totalPositiveMaxActivePowerTime and date_add('1900-01-01',interval floor(datediff(str_to_date(a.clientOperationTime, '%Y%m%d%H%i%s'),'1900-01-01')/7)*7 day) = b.weekstart  and date_add('1900-01-01',interval floor(datediff(str_to_date(a.clientOperationTime, '%Y%m%d%H%i%s'),'1900-01-01')/7)*7+6 day) = b.weekend  and b.area_id = c.area_id and b.concentrator_id = c.concentrator_id and b.pn = c.pn
		) dm where dp.clientOperationTime = dm.clientOperationTime and dp.area_id = dm.area_id and dp.concentrator_id = dm.concentrator_id and dp.pn = dm.pn 
    ) ct where 1=1
    
		  and ct.area_id = #{areaId ,jdbcType=VARCHAR} 
	
	
		  and ct.concentrator_id in
		    
	      		#{item}  
	      
	
	
		  and #{date ,jdbcType=VARCHAR} >= ct.weekstart AND #{date ,jdbcType=VARCHAR} <= ct.weekend
	
	
		  and ct.pn = #{pn ,jdbcType=VARCHAR}
	
	order by ct.weekstart desc
	
      limit ${limitStart} , ${limitEnd}

