mapper.select:

select

  distinct


from t_300_report_energy_by_hour




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

SELECT t1.total, t2.`name`, t1.p_00, t1.p_01, t1.p_02, t1.p_03, t1.p_04, t1.p_05, t1.p_06, t1.p_07, t1.p_08, t1.p_09, t1.p_10, t1.p_11, t1.p_12, t1.p_13, t1.p_14, t1.p_15, t1.p_16, t1.p_17, t1.p_18, t1.p_19, t1.p_20, t1.p_21, t1.p_22, t1.p_23 FROM t_300_report_energy_by_hour t1, t_202_pn_info t2 WHERE t1.area_id = t2.area_id AND t1.concentrator_id = t2.concentrator_id AND t1.pn = t2.pn
AND t1.operation_time = #{date ,jdbcType=VARCHAR}
order by t1.operation_time desc
limit ${limitStart} , ${limitEnd}
